mapper.select:
SELECT b.board_no boardNo, b.emp_no empNo, b.board_category boardCategory, b.board_title boardTitle, b.board_content boardContent, b.board_pinned boardPinned, b.board_count boardCount, b.createdate, b.updatedate, b.create_id createId, b.update_id updateId, ei.emp_name empName FROM board b INNER JOIN emp_info ei ON b.emp_no = ei.emp_no WHERE b.board_category = #{category}
AND b.board_title LIKE CONCAT('%', #{searchText}, '%')
AND b.board_content LIKE CONCAT('%', #{searchText}, '%')
ORDER BY b.createdate DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM board WHERE board_category = #{category}
AND board_title LIKE CONCAT('%', #{searchText}, '%')
AND board_content LIKE CONCAT('%', #{searchText}, '%')

mapper.update:
UPDATE board
SET board_count = board_count + 1
WHERE board_no = #{boardNo}

mapper.select:
SELECT * FROM board
(boardCategory = #{category} and ${searchOption} LIKE CONCAT('%', #{searchText}, '%'))
ORDER BY board_no DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.update:
UPDATE board SET board_title = #{boardTitle}, board_content = #{boardContent},
board_pinned = #{boardPinned},
updatedate = NOW(), update_id = #{empNo} WHERE board_no = #{boardNo} AND emp_no = #{empNo}

mapper.select:
SELECT board_category
FROM board
WHERE board_no = #{boardNo}
