mapper.select:
		SELECT 
			COUNT(*) 
		FROM emp_info
 		WHERE 
 			1=1

		        AND emp_no = #{empNo}


		        AND emp_name = #{empName}


		        AND dept = #{dept}

SELECT e.emp_no empNo, e.emp_name empName, dc.code_name dept, al.annual_count annualCount FROM emp_info e LEFT OUTER JOIN annual_leave al ON e.emp_no = al.emp_no INNER JOIN common_code dc ON e.dept = dc.code WHERE 1=1
AND e.emp_no = #{empNo}
AND e.emp_name = #{empName}
AND dc.code = #{dept}
ORDER BY e.emp_no DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT 
	count(*)
FROM leave_recode lr
LEFT OUTER JOIN emp_info ei ON lr.emp_no = ei.emp_no
LEFT OUTER JOIN account_list al ON lr.emp_no = al.emp_no
WHERE
	1=1

    	AND lr.emp_no = #{empNo}


    	AND ei.emp_name = #{empName}


    	AND lr.leave_type = #{leaveType}


    	AND YEAR(lr.leave_start_date) = #{year}


    	AND lr.leave_start_date BETWEEN #{startDate} AND #{endDate}


    	AND al.active = #{active}

SELECT lr.emp_no empNo, dc.code_name dept, ei.emp_name empName, lc.code_name leaveType, lr.leave_start_date startDate, lr.leave_end_date endDate, lr.leave_period_date leavePeriodDate, ac.code_name active FROM leave_recode lr LEFT OUTER JOIN emp_info ei ON lr.emp_no = ei.emp_no LEFT OUTER JOIN account_list al ON lr.emp_no = al.emp_no INNER JOIN common_code ac ON al.active = ac.code INNER JOIN common_code lc ON lr.leave_type = lc.code INNER JOIN common_code dc ON ei.dept = dc.code WHERE 1=1
AND lr.emp_no = #{empNo}
AND ei.emp_name = #{empName}
AND lr.leave_type = #{leaveType}
AND YEAR(lr.leave_start_date) = #{year}
AND lr.leave_start_date BETWEEN #{startDate} AND #{endDate}
AND al.active = #{active}
ORDER BY ei.emp_no DESC LIMIT #{beginRow}, #{rowPerPage}


SELECT 
	COUNT(*)
FROM commute c
LEFT OUTER JOIN emp_info e ON c.emp_no = e.emp_no
WHERE
	1=1

    	AND e.emp_no = #{empNo}


    	AND e.emp_name = #{empName}


     	AND dc.code = #{dept}


    	AND YEAR(c.emp_on_off_date) = #{year}


    	AND c.emp_on_off_date BETWEEN #{startDate} AND #{endDate}

SELECT 
	e.emp_no empNo, 
	e.emp_name empName, 
	dc.code_name dept, 
	c.emp_on_off_date empOnOffDate, 
	c.emp_on_time empOnTime, 
	c.emp_off_time empOffTime
FROM commute c
LEFT OUTER JOIN emp_info e ON c.emp_no = e.emp_no
INNER JOIN common_code dc ON e.dept = dc.code
WHERE
	1=1

    	AND e.emp_no = #{empNo}


    	AND e.emp_name = #{empName}


     	AND dc.code = #{dept}


    	AND YEAR(c.emp_on_off_date) = #{year}


    	AND c.emp_on_off_date BETWEEN #{startDate} AND #{endDate}

ORDER BY c.emp_on_off_date DESC, c.createdate DESC
LIMIT #{beginRow}, #{rowPerPage}
