mapper.select:
SELECT COUNT(*) FROM document_default WHERE (doc_status IN('A0201', 'A0202') OR (doc_stamp2 IS NULL AND doc_stamp3 IS NULL AND doc_status = 'A0204')) AND emp_no = #{empNo}
AND doc_title LIKE CONCAT('%', #{docTitle}, '%')
AND createdate BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(*) FROM document_default WHERE doc_status = 'A0203' AND receiver_team = #{dept}
AND d.doc_title LIKE CONCAT('%', #{writer}, '%')
AND d.doc_title LIKE CONCAT('%', #{docTitle}, '%')
AND createdate BETWEEN #{startDate} AND #{endDate}

SELECT d.doc_no docNo, d.createdate createdate, doc.code_name category, doc.code docCode, d.doc_title docTitle, d.emp_no empNo, d.writer writer, sta.code_name docStatus FROM document_default d INNER JOIN common_code sta ON d.doc_status = sta.code INNER JOIN common_code doc ON d.category = doc.code WHERE (d.doc_status IN('A0201', 'A0202') OR (d.doc_stamp2 IS NULL AND d.doc_stamp3 IS NULL AND d.doc_status = 'A0204')) AND d.emp_no = #{empNo}
AND d.doc_title LIKE CONCAT('%', #{docTitle}, '%')
AND d.createdate BETWEEN #{startDate} AND #{endDate}
ORDER BY d.createdate DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT d.doc_no docNo, d.createdate createdate, doc.code_name category, doc.code docCode, d.doc_title docTitle, d.emp_no empNo, d.writer writer, sta.code_name docStatus FROM document_default d LEFT OUTER JOIN document_signer ds ON d.doc_no = ds.doc_no INNER JOIN common_code sta ON d.doc_status = sta.code INNER JOIN common_code doc ON d.category = doc.code WHERE ((ds.emp_signer_level = 1) OR (ds.emp_signer_level = 2 AND d.doc_stamp2 IS NOT NULL)) AND ds.doc_status IN('A0201', 'A0204') AND (ds.emp_signer_no = #{empNo} OR d.reference = #{empNo})
AND d.doc_title LIKE CONCAT('%', #{writer}, '%')
AND d.doc_title LIKE CONCAT('%', #{docTitle}, '%')
AND d.createdate BETWEEN #{startDate} AND #{endDate}
ORDER BY d.createdate DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT d.doc_no docNo, d.createdate createdate, doc.code_name category, doc.code docCode, d.doc_title docTitle, d.emp_no empNo, d.writer writer, sta.code_name docStatus FROM document_default d INNER JOIN common_code sta ON d.doc_status = sta.code INNER JOIN common_code doc ON d.category = doc.code WHERE (doc_status = 'A0203' AND emp_no = #{empNo}) OR (1=1 AND EXISTS ( SELECT 1 FROM document_signer ds WHERE ds.doc_no = d.doc_no AND ds.doc_status = 'A0203' AND ds.emp_signer_level IN (1, 2) AND ds.emp_signer_no = #{empNo} ) )
AND d.doc_title LIKE CONCAT('%', #{writer}, '%')
AND d.doc_title LIKE CONCAT('%', #{docTitle}, '%')
AND d.createdate BETWEEN #{startDate} AND #{endDate}
ORDER BY d.createdate DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT d.doc_no docNo, d.createdate createdate, doc.code_name category, doc.code docCode, d.doc_title docTitle, d.emp_no empNo, d.writer writer, sta.code_name docStatus FROM document_default d INNER JOIN common_code sta ON d.doc_status = sta.code INNER JOIN common_code doc ON d.category = doc.code WHERE d.doc_status = 'A0203' AND d.receiver_team = #{dept}
AND d.doc_title LIKE CONCAT('%', #{writer}, '%')
AND d.doc_title LIKE CONCAT('%', #{docTitle}, '%')
AND d.createdate BETWEEN #{startDate} AND #{endDate}
ORDER BY d.createdate DESC LIMIT #{beginRow}, #{rowPerPage}
