mapper.select:
SELECT emp_name empName
FROM emp_info 
WHERE emp_no = #{empNo}

SELECT COUNT(*)
FROM emp_info
WHERE emp_no = #{empNo} AND RIGHT(emp_jumin_no, 7) = #{juminNo}

mapper.update:
UPDATE account_list 
SET emp_pw = PASSWORD(#{newPw}), 
	updatedate = NOW(),
	update_id = #{empNo}
WHERE emp_no = #{empNo}
