mapper.select:
SELECT r.rev_no revNo, r.emp_no empNo, r.equip_no equipNo, r.equip_category equipCategory, r.rev_start_time revStartTime, r.rev_end_time revEndTime, r.rev_status revStatus, r.rev_reason revReason, r.createdate, r.updatedate, r.create_id createId, r.update_id updateId, m.room_name roomName FROM reservation r INNER JOIN meeting_room m ON r.equip_no = m.room_no AND r.equip_category = m.equip_category WHERE r.emp_no = #{empNo} AND r.equip_category = 'E0101'
AND DATE(r.rev_start_time) >= #{revStartTime} AND DATE(r.rev_start_time) <= #{revEndTime}
AND DATE(r.rev_start_time) >= #{revStartTime}
AND 1 = 1
ORDER BY r.createdate DESC;

mapper.update:
UPDATE reservation
SET rev_status = #{revStatus},
updatedate = NOW(),
update_id = #{updateId}
WHERE rev_no = #{revNo};

mapper.select:
SELECT r.rev_no revNo, r.emp_no empNo, e.emp_name empName, r.equip_no equipNo, r.equip_category equipCategory, r.rev_start_time revStartTime, r.rev_end_time revEndTime, r.rev_status revStatus, r.rev_reason revReason, r.createdate, r.updatedate, r.create_id createId, r.update_id updateId, u.emp_name updateName, m.room_name roomName FROM reservation r INNER JOIN meeting_room m ON r.equip_no = m.room_no AND r.equip_category = m.equip_category INNER JOIN emp_info e ON r.emp_no = e.emp_no LEFT JOIN emp_info u ON r.update_id = u.emp_no
AND e.emp_name = #{empName}
AND r.rev_status = #{revStatus}
AND DATE(r.rev_start_time) >= #{revStartTime} AND DATE(r.rev_end_time) <= #{revEndTime}
AND DATE(r.rev_start_time) >= #{revStartTime}
AND 1 = 1
ORDER BY r.createdate DESC;
