mapper.select:
SELECT COUNT(*)
FROM project


		AND (
			project_name LIKE CONCAT('%', #{searchName}, '%')
			OR emp_no IN (SELECT emp_no FROM emp_info WHERE emp_name LIKE CONCAT('%', #{searchName}, '%'))
		)


		AND project_start_date >= #{startDate}


		AND project_end_date <= #{endDate}

SELECT project_no projectNo, emp_no empNo, project_name projectName, project_color projectColor, project_start_date projectStartDate, project_end_date projectEndDate, project_content projectContent, project_status projectStatus, createdate, updatedate, create_id createId, update_id updateId FROM project
AND ( project_name LIKE CONCAT('%', #{searchName}, '%') OR emp_no IN (SELECT emp_no FROM emp_info WHERE emp_name LIKE CONCAT('%', #{searchName}, '%')) )
AND project_start_date >= #{startDate}
AND project_end_date <= #{endDate}
ORDER BY
createdate DESC, project_no ASC LIMIT #{startRow}, #{rowPerPage}

SELECT p.project_no projectNo, p.emp_no empNo, pm.emp_no memberNo, p.project_name projectName, p.project_color projectColor, p.project_start_date projectStartDate, p.project_end_date projectEndDate, p.project_content projectContent, p.project_status projectStatus, p.createdate, p.updatedate, p.create_id createId, p.update_id updateId FROM project p INNER JOIN project_member pm ON p.project_no=pm.project_no WHERE pm.emp_no = #{empNo} AND p.project_status= 'A0502'
AND ( project_name LIKE CONCAT('%', #{searchName}, '%') OR p.emp_no IN (SELECT emp_no FROM emp_info WHERE emp_name LIKE CONCAT('%', #{searchName}, '%')) )
AND project_start_date >= #{startDate}
AND project_end_date <= #{endDate}
ORDER BY
p.createdate DESC, p.project_no ASC LIMIT #{startRow}, #{rowPerPage}

SELECT project_no projectNo, emp_no empNo, project_name projectName, project_color projectColor, project_start_date projectStartDate, project_end_date projectEndDate, project_content projectContent, project_status projectStatus, createdate, updatedate, create_id createId, update_id updateId FROM project WHERE project_status= 'A0503'
AND ( project_name LIKE CONCAT('%', #{searchName}, '%') OR emp_no IN (SELECT emp_no FROM emp_info WHERE emp_name LIKE CONCAT('%', #{searchName}, '%')) )
AND project_start_date >= #{startDate}
AND project_end_date <= #{endDate}
ORDER BY createdate DESC, projectNo ASC LIMIT #{startRow}, #{rowPerPage}

mapper.delete:
DELETE FROM project_member
WHERE 
	emp_no = #{empNo}
	AND 
	project_no = #{projectNo};
